import React, { Component } from 'react';
import logo from './logo.svg';
import './index.css';

class Card extends Component {
  constructor(props) {
    super(props);
    this.state = {
      match: false,
      restaurant: {

      },
      counter: 0
      // name: "Berkeley Social Club",
      // location: "2050 University Ave",
      // rating: 4,
      // food: "Breakfast Food",
      // price: "$$",
      // picture: "https://www.berkeleyside.com/wp-content/uploads/2017/01/Berkeley-Social-Club-BI6A5607.jpg",
      // liked: false
    };
    this.cardStyle = {
      background: "linear-gradient(180deg, rgba(255, 255, 255, 0) 0%, #FFFFFF 79.17%), url(" + this.state.restaurant.PICTURE + ")"
    };
  }

  async reject(event) {
    event.preventDefault();
    this.setState({counter: this.state.counter + 1});
    let response = await fetch("http://localhost:5000", {
      method: "POST",
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(this.state)
    })
    let data = await response.json();
    data = JSON.stringify(data);
    data = JSON.parse(data);
    this.setState({restaurant: data});
  }

  async like(event) {
    event.preventDefault();
    this.setState({counter: this.state.counter + 1});
    this.setState({match: true});
  }

  render () {
    return (
      <div class="card" style={{backgroundImage: "linear-gradient(180deg, rgba(255, 255, 255, 0) 0%, #FFFFFF 80%), url(" + this.state.restaurant.PICTURE + ")"}}>
            <h2 class="name">{this.state.restaurant.NAME}</h2>
            <p class="type-cost">{this.state.restaurant['TYPE OF FOOD']} | {this.state.restaurant.PRICE} | Rating: {this.state.restaurant.YELP_RATING}/5</p>
            <p class="address">{this.state.restaurant.LOCATION}</p>
            <form onSubmit={e=>this.like(e)}>
              <input type="image" src="assets/img/yes.png" name="like" value="Like" alt="Submit"></input>
            </form>
            <form onSubmit={e=>this.reject(e)}>
              <input type="image" src="assets/img/no.png" name="reject" value="Reject" alt="Submit"></input>
            </form>
      </div>
    );
  }

}

export default Card;
